  c_ext, dt_create_time, dt_update_time
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{path,jdbcType=VARCHAR}, 
  #{size,jdbcType=BIGINT}, #{suffix,jdbcType=VARCHAR}, #{ownerId,jdbcType=BIGINT}, 
  #{uploaderIp,jdbcType=VARCHAR}, #{systemId,jdbcType=VARCHAR}, #{valid,jdbcType=INTEGER}, 
  #{ext,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

